let todoForm=document.getElementById("todo-list"),inputField=document.querySelector(".input-field"),todosContainer=document.querySelector(".todos"),themeButton=document.getElementById("toggle-btn");function createTask(e){let t=document.createElement("li");t.innerHTML=`
        <div>
            <input type="checkbox" name="tasks">
            <span>${e}</span>
        </div>
        <button class="delete-btn"><i class="ri-delete-bin-fill"></i></button>
    `,t.querySelector(".delete-btn").addEventListener("click",()=>{t.remove(),deleteTask(e)}),todosContainer.appendChild(t)}function saveTask(e){var t=JSON.parse(localStorage.getItem("tasks"))||[];t.push(e),localStorage.setItem("tasks",JSON.stringify(t))}function deleteTask(t){let e=JSON.parse(localStorage.getItem("tasks"))||[];e=e.filter(e=>e!==t),localStorage.setItem("tasks",JSON.stringify(e))}function enableDarkMode(){document.body.classList.add("darkmode"),localStorage.setItem("darkmodeStatus","active")}function disableDarkMode(){document.body.classList.remove("darkmode"),localStorage.setItem("darkmodeStatus","inactive")}function updateGreetingImage(){var e=document.getElementById("greetings-img"),t=(new Date).getHours();let a="morning.jpg";a=5<=t&&t<12?"morning.jpg":12<=t&&t<17?"good_afternoon.jpg":17<=t&&t<20?"good evening.jpg":"good night.jpg",e.src="assets/images/"+a}window.addEventListener("DOMContentLoaded",()=>{(JSON.parse(localStorage.getItem("tasks"))||[]).forEach(e=>createTask(e));var e=localStorage.getItem("darkmodeStatus");("active"===e?enableDarkMode:disableDarkMode)()}),todoForm.addEventListener("submit",e=>{e.preventDefault();e=inputField.value.trim();e&&(createTask(e),saveTask(e),inputField.value="")}),themeButton.addEventListener("click",()=>{("active"!==localStorage.getItem("darkmodeStatus")?enableDarkMode:disableDarkMode)()}),window.addEventListener("DOMContentLoaded",()=>{updateGreetingImage()});